    create_success_alert,
    create_error_alert,
    create_warning_alert,
    create_info_alert
)


Functions
def _create_auto_dismiss_script(
    timeout_ms:int=3000 # Time in milliseconds before auto-dismiss
) -&gt; FT: # Script element for auto-dismissing alerts
